import React, {Component} from 'react';
import BookingList from "../bookings/BookingList.js";
import {Link} from 'react-router-dom'

class CustomerDetail extends Component{
    constructor(props){
        super(props);
        this.state={
            customer: props.customer,
            bookings: []
        }
    }
    onDelete(){
        this.state.handleDelete(this.state.customer.id)
    }

    onEdit(){
        this.state.handleEdit(this.state.customer.id)
    }

    componentDidMount(){
        if(this.props.customer._embedded.bookings){
            this.setState({bookings: this.props.customer.customer._embedded.bookings})
        }
        if(this.props.customer){
            this.setState({customer: this.props.customer})
        }
    }

    render(){
    return(
        <div className="customer-item-details">
            <div className="customer-item-component-details">
                <h4>Customer Details:</h4>
                <p>Name: {this.state.customer.firstName} {this.state.customer.surname}</p>
                <p>Address: {this.state.customer.address} </p>
                <p>Contact: {this.state.customer.contact} </p> 
                <p>Gender: {this.state.customer.gender} </p>      
                <p>Bill: â‚¦ {this.state.customer.bill} </p>
            </div>
            <div className="customer-item-component-details">
                <h4>Associated Bookings:</h4>
                <BookingList bookings={this.state.bookings}/>
            </div>
            <button className="delete-button" onClick={this.onDelete}>Delete Customer</button>
            <button className="edit-button" onClick={this.onEdit}>Edit Customer</button>
        </div>
    )}
}

export default CustomerDetail;
