import React from 'react';
import BookingList from '../bookings/BookingList';

const MeasurementDetail = (props) => {

    if(!props.measurement){
        return null;
    }

    const onDelete = () => {
        props.handleMeasurementDelete(props.id)
    }

    const onEdit = () => {
        props.handleMeasurementEdit(props.onEdit)
    }

    const measurementItems = Object.keys(props.measurement).map((measure) =>{
        return <div key={measure.charAt(0).toUpperCase()+ measure.slice(1)} className="measurement-items-values"> {props.measurement[measure]}</div> 
    })

    return(
        <div className="measurement-item-details">
            <div className="measurement-item-component-details">
                <h4>Measurement Details:</h4>
                <p>{measurementItems}</p>
            </div>
            <div className="measurement-item-component-details">
                <h4>Bookings:</h4> 
                <BookingList bookings = {props.measurement._embedded.bookings}/>
            </div>
            <button className="delete-button" onClick={onDelete}>Delete Measurement</button>
            <button className="edit-button" onClick={onEdit}>Edit Measurement</button>
        </div>
    )

}

export default MeasurementDetail;
