import React from 'react';
import {Link} from "react-router-dom";
import BookingDetail from './BookingDetail';

const Booking = (props) => {
    if(!props.booking){
        return null;
    }
    //this function will iterate through the object (measurment) and pull all keys and values out for listing.
    const measurementItems = Object.keys(props.measurement).map((measure) => {
        return <div className="measurement-items-values">{measure.charAt(0).toUpperCase()+ measure.slice(1)}:  {props.measurement[measure]}</div> 
    })

    const handleBookingDetail=()=>{
        if(!props.booking){
            return null
        } 
        return(
        <BookingDetail booking = {props.booking}/>
        )}

        return(
            <React.Fragment>
                <Link to={"/bookings/"+props.booking.id} className="name" onClick={handleBookingDetail}>
                    Booking_ID: {props.booking.id} 
                    <p className="booking-list-display">Customer: {props.booking.customer.firstName} {props.booking.customer.surname}</p>
                <p className="booking-list-display">Style: {props.booking.style.name}</p>
                <p className="booking-list-display">Style: {props.booking.style.image}</p>
                <p className="booking-list-display">TotalCost: {"₦ " + props.booking.totalCost}</p>   
                <p className="booking-list-display">Status: {props.booking.status}</p>    
                <p className="booking-list-display">Remarks: {props.booking.remarks}</p>                 
                </Link>
                <p className="measurement-map-items">
                Customer Measurement (inches):
                    <ul>{measurementItems}</ul>
                </p>
            </React.Fragment>

        )
    }

export default Booking;
