import React from 'react';
import Customer from '../customers/Customer.js';

const BookingDetail = (props) => {

    const onDelete=()=> {
        props.handleDelete(props.booking.id)
    }

    const onEdit=() => {
        props.handleEdit(props.booking.id)
    }

    if(!props.booking){
        return null
    }

    return(
        <div>
            <ul>
            <h6 className="booking-item-basic-details">Booking Basic Info:</h6>
                <p>Id: {props.booking.id}</p>
                <p>Date: {props.booking.date}</p>
                <p>Cost: {props.booking.totalCost}</p>
                <p>Status: {props.booking.status}</p>   
                <p>Remarks: {props.booking.date}</p>            
            </ul>
            <ul>
            <h6 className="booking-item-basic-details">Customer Info:</h6>
                <Customer customer={props.customer}/>
            </ul>
           <button onClick={onDelete}>Delete Booking</button>
           <button onClick={onEdit}>Edit Booking</button>
        </div>
    )
}

export default BookingDetail;
