import React, {Component} from 'react';
import Request from '../../helpers/request';
import FabricDetail from '../../components/fabrics/FabricDetail';

class SingleFabricContainer extends Component{
    constructor(props){
        super(props);
        this.state={
            fabric: null
        }
    }

    componentDidMount(){
        const request = new Request();
        const url = '/api/fabrics/'+this.props.id+'?projection=embedded';
        request.get(url).then((data)=>{
            this.setState({fabric: data});
        })

    }

    render(){
        return(
            <FabricDetail id={this.props.id} fabric={this.state.fabric} bookings={this.fabric.bookings}/>
        )
    }

}

export default SingleFabricContainer;
