import React, {Fragment, useState} from 'react';
import {genderCategories} from './../../constants/GenderConstant';
import {maleStyleCategories, femaleStyleCategories, girlStyleCategories, boyStyleCategories} from './../../constants/StyleConstants';

const StyleFormNew = (props) => {

    const [file, setFile] = useState('');
    const [genderCategory, setGenderCategory] = useState('')
    const [styleCategory, setStylecategory] = useState('');

    const onSelectGender = e => {
        setGenderCategory(e.target.value)
        styleCat();
        console.log(e.target.value);

    }

    const onSelectStyleCategory = e => {
        setStylecategory(e.target.value)
    }

    const onChange = e =>{
        setFile(e.target.files[0])
    } 

    const handleSubmit=(event)=>{
        event.preventDefault()
        const newStyle = {
            "name": event.target.name.value,
            "image": file,
            "labourCost": event.target.labourCost.value,
            "source": event.target.source.value,
            "gender": genderCategory,
            "styleCategory": styleCategory,
            "selectedStyle": null
        }
        console.log(genderCategory);  
        props.handleStylePost(newStyle);
    }

    const genderCat = genderCategories.map((x, y) => {
        return <option key={y} value={x}>{x}</option>
    })

    const styleCat = () => {
        console.log(genderCategory);

        if(genderCategory === "men"){
            maleStyleCategories.map((x, y) => {
                return <option key={y} value = {x}>{x}</option>
            })
        } if(genderCategory === "women"){
            femaleStyleCategories.map((x,y)=> {
                return <option key={y} value={x}>{x}</option>
            })
        } if(genderCategory === "boys"){
            boyStyleCategories.map((x,y)=> {
                return <option key={y} value={x}>{x}</option>
            })
        } else {
            girlStyleCategories.map((x,y)=>{
                return <option key={y} value={x}>{x}</option>
            })
        }
    }


    return(
        <Fragment>
            <div>
                <form onSubmit= {handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Style Name</label>
                        <input type="text" className="form-control" id="name" placeholder="Style Name"/>

                        <label htmlFor="labourCost">Cost</label>
                        <input type="text" className="form-control" id="labourCost" placeholder="Style sowing cost"/>

                        <label htmlFor="source">Designer</label>
                        <input type="text" className="form-control" id="source" placeholder="Style Designer"/>

                        <label htmlFor="genderCategory">Gender Category</label>
                        <select name="gender" onChange={onSelectGender}>
                            <option disabled selected>Select Gender Category</option>
                        {genderCat}
                        </select>

                        <label htmlFor="selectedCategory"> Style Category</label>
                        <select name="category" onChange={onSelectStyleCategory}>  
                        <option disabled selected>Select Style Category</option>
                        {styleCat}
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="exampleFormControlFile1">Picture</label>
                        <input type="file" 
                        className="form-control-file" 
                        id="exampleFormControlFile1" 
                        onChange = {onChange}
                        />
                    </div>
                    <button type="submit">Save</button>
                </form>
            </div>
        </Fragment>
    )



}

export default StyleFormNew;
